mapper.select:
select COUNT(nt_leave.PER_ID) totalNum,IFNULL(SUM(nt_leave.LEAVE_COUNT), 0) totalHours FROM nt_leave LEFT JOIN nt_personnel ON nt_leave.PER_ID = nt_personnel.PER_ID LEFT JOIN nt_department ON nt_personnel.DEPT_ID = nt_department.DEPT_ID
and nt_leave.LEAVE_TYPE =#{leaveType}
and nt_personnel.NAME like CONCAT('%',#{userName},'%')
and nt_department.DEPT_ID=#{deptId}
and nt_leave.BEG_DATE>=#{begDate}
order by nt_leave.LEAVE_ID desc

select , nt_personnel.`NAME` userName, nt_department.DEPT_NAME deptName FROM nt_leave LEFT JOIN nt_personnel ON nt_leave.PER_ID = nt_personnel.PER_ID LEFT JOIN nt_department ON nt_personnel.DEPT_ID = nt_department.DEPT_ID
and nt_personnel.NAME like CONCAT('%',#{userName},'%')
and nt_department.DEPT_ID=#{deptId}
and nt_leave.BEG_DATE>=#{begDate}
order by nt_leave.LEAVE_ID desc
